mapper.update:
UPDATE ALBUM_CONTRIBUTOR

  UPDATE_DATETIME = current_timestamp(),
  COMMENT = #{comment},
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},

WHERE 
   ALBUM_ID = #{albumId}
   AND TRACK_NO = #{trackNo}
   AND CONTRIBUTOR = #{contributor}
   AND UPDATE_DATETIME = #{updateDatetime}

UPDATE ALBUM_CONTRIBUTOR

  UPDATE_DATETIME = current_timestamp(),
  COMMENT = #{comment},
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},

WHERE 1 = 1 
  AND ALBUM_ID = #{albumId}
  AND TRACK_NO = #{trackNo}
  AND CONTRIBUTOR = #{contributor}
  AND COMMENT = #{comment}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

UPDATE ALBUM_CONTRIBUTOR
SET
  COMMENT = #{comment}
  ,CREATE_AUTHOR = #{createAuthor}
  ,CREATE_DATETIME = #{createDatetime}
  ,UPDATE_AUTHOR = #{updateAuthor}
  ,UPDATE_DATETIME = current_timestamp() 
WHERE 
   ALBUM_ID = #{albumId}
   AND TRACK_NO = #{trackNo}
   AND CONTRIBUTOR = #{contributor}
   AND UPDATE_DATETIME = #{updateDatetime}

mapper.delete:
DELETE FROM ALBUM_CONTRIBUTOR
WHERE
   ALBUM_ID = #{albumId}
   AND TRACK_NO = #{trackNo}
   AND CONTRIBUTOR = #{contributor}
   AND UPDATE_DATETIME = #{updateDatetime}

DELETE FROM ALBUM_CONTRIBUTOR
WHERE 1 = 1 
  AND ALBUM_ID = #{albumId}
  AND TRACK_NO = #{trackNo}
  AND CONTRIBUTOR = #{contributor}
  AND COMMENT = #{comment}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ALBUM_ID AS "albumId"
  ,TRACK_NO AS "trackNo"
  ,CONTRIBUTOR AS "contributor"
  ,COMMENT AS "comment"
  ,CREATE_AUTHOR AS "createAuthor"
  ,CREATE_DATETIME AS "createDatetime"
  ,UPDATE_AUTHOR AS "updateAuthor"
  ,UPDATE_DATETIME AS "updateDatetime"
FROM ALBUM_CONTRIBUTOR
WHERE 1 = 1 
  AND ALBUM_ID = #{albumId}
  AND TRACK_NO = #{trackNo}
  AND CONTRIBUTOR = #{contributor}
  AND COMMENT = #{comment}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}
   ORDER BY ${theOrderByCondition}
   GROUP BY ${theGroupByCondition}
  LIMIT ${theFetchStart}, ${theFetchSize}

SELECT count(*)
FROM ALBUM_CONTRIBUTOR
WHERE 1 = 1 
  AND ALBUM_ID = #{albumId}
  AND TRACK_NO = #{trackNo}
  AND CONTRIBUTOR = #{contributor}
  AND COMMENT = #{comment}
