mapper.update:
UPDATE ALBUM_GENRE
UPDATE_DATETIME = current_timestamp(), GENRE_NAME = #{genreName}, COMMENT = #{comment}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor},
WHERE GENRE_ID = #{genreId} AND UPDATE_DATETIME = #{updateDatetime}

UPDATE ALBUM_GENRE

  UPDATE_DATETIME = current_timestamp(),
  GENRE_NAME = #{genreName},
  COMMENT = #{comment},
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},

WHERE 1 = 1 
  AND GENRE_ID = #{genreId}
  AND GENRE_NAME = #{genreName}
  AND COMMENT = #{comment}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

UPDATE ALBUM_GENRE
SET
  GENRE_NAME = #{genreName}
  ,COMMENT = #{comment}
  ,CREATE_AUTHOR = #{createAuthor}
  ,CREATE_DATETIME = #{createDatetime}
  ,UPDATE_AUTHOR = #{updateAuthor}
  ,UPDATE_DATETIME = current_timestamp() 
WHERE 
   GENRE_ID = #{genreId}
   AND UPDATE_DATETIME = #{updateDatetime}

mapper.delete:
DELETE FROM ALBUM_GENRE
WHERE
   GENRE_ID = #{genreId}
   AND UPDATE_DATETIME = #{updateDatetime}

DELETE FROM ALBUM_GENRE
WHERE 1 = 1 
  AND GENRE_ID = #{genreId}
  AND GENRE_NAME = #{genreName}
  AND COMMENT = #{comment}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  GENRE_ID AS "genreId"
  ,GENRE_NAME AS "genreName"
  ,COMMENT AS "comment"
  ,CREATE_AUTHOR AS "createAuthor"
  ,CREATE_DATETIME AS "createDatetime"
  ,UPDATE_AUTHOR AS "updateAuthor"
  ,UPDATE_DATETIME AS "updateDatetime"
FROM ALBUM_GENRE
WHERE 1 = 1 
  AND GENRE_ID = #{genreId}
  AND GENRE_NAME = #{genreName}
  AND COMMENT = #{comment}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}
   ORDER BY ${theOrderByCondition}
   GROUP BY ${theGroupByCondition}
  LIMIT ${theFetchStart}, ${theFetchSize}

SELECT count(*)
FROM ALBUM_GENRE
WHERE 1 = 1 
  AND GENRE_ID = #{genreId}
  AND GENRE_NAME = #{genreName}
  AND COMMENT = #{comment}
