mapper.update:
UPDATE ALBUM
UPDATE_DATETIME = current_timestamp(), TITLE = #{title}, ARTIST = #{artist}, ISSUE_DATE = #{issueDate}, GENRE = #{genre}, COVER_PHOTO = #{coverPhoto}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor},
WHERE ID = #{id} AND UPDATE_DATETIME = #{updateDatetime}

UPDATE ALBUM

  UPDATE_DATETIME = current_timestamp(),
  TITLE = #{title},
  ARTIST = #{artist},
  ISSUE_DATE = #{issueDate},
  GENRE = #{genre},
  COVER_PHOTO = #{coverPhoto},
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},

WHERE 1 = 1 
  AND ID = #{id}
  AND TITLE = #{title}
  AND ARTIST = #{artist}
  AND ISSUE_DATE = #{issueDate}
  AND GENRE = #{genre}
  AND COVER_PHOTO = #{coverPhoto}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

UPDATE ALBUM
SET
  TITLE = #{title}
  ,ARTIST = #{artist}
  ,ISSUE_DATE = #{issueDate}
  ,GENRE = #{genre}
  ,COVER_PHOTO = #{coverPhoto}
  ,CREATE_AUTHOR = #{createAuthor}
  ,CREATE_DATETIME = #{createDatetime}
  ,UPDATE_AUTHOR = #{updateAuthor}
  ,UPDATE_DATETIME = current_timestamp() 
WHERE 
   ID = #{id}
   AND UPDATE_DATETIME = #{updateDatetime}

mapper.delete:
DELETE FROM ALBUM
WHERE
   ID = #{id}
   AND UPDATE_DATETIME = #{updateDatetime}

DELETE FROM ALBUM
WHERE 1 = 1 
  AND ID = #{id}
  AND TITLE = #{title}
  AND ARTIST = #{artist}
  AND ISSUE_DATE = #{issueDate}
  AND GENRE = #{genre}
  AND COVER_PHOTO = #{coverPhoto}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ID AS "id"
  ,TITLE AS "title"
  ,ARTIST AS "artist"
  ,ISSUE_DATE AS "issueDate"
  ,GENRE AS "genre"
  ,COVER_PHOTO AS "coverPhoto"
  ,CREATE_AUTHOR AS "createAuthor"
  ,CREATE_DATETIME AS "createDatetime"
  ,UPDATE_AUTHOR AS "updateAuthor"
  ,UPDATE_DATETIME AS "updateDatetime"
FROM ALBUM
WHERE 1 = 1 
  AND ID = #{id}
  AND TITLE = #{title}
  AND ARTIST = #{artist}
  AND ISSUE_DATE = #{issueDate}
  AND GENRE = #{genre}
  AND COVER_PHOTO = #{coverPhoto}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}
   ORDER BY ${_theOrderByCondition}
  LIMIT ${_theFetchStart}, ${_theFetchSize}

SELECT count(*)
FROM ALBUM
WHERE 1 = 1 
  AND ID = #{id}
  AND TITLE = #{title}
  AND ARTIST = #{artist}
  AND ISSUE_DATE = #{issueDate}
  AND GENRE = #{genre}
  AND COVER_PHOTO = #{coverPhoto}
