mapper.update:
UPDATE ALBUM_TRACK
UPDATE_DATETIME = current_timestamp(), TRACK_NAME = #{trackName}, PLAY_TIME = #{playTime}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor},
WHERE ALBUM_ID = #{albumId} AND TRACK_NO = #{trackNo} AND UPDATE_DATETIME = #{updateDatetime}

UPDATE ALBUM_TRACK

  UPDATE_DATETIME = current_timestamp(),
  TRACK_NAME = #{trackName},
  PLAY_TIME = #{playTime},
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},

WHERE 1 = 1 
  AND ALBUM_ID = #{albumId}
  AND TRACK_NO = #{trackNo}
  AND TRACK_NAME = #{trackName}
  AND PLAY_TIME = #{playTime}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

UPDATE ALBUM_TRACK
SET
  TRACK_NAME = #{trackName}
  ,PLAY_TIME = #{playTime}
  ,CREATE_AUTHOR = #{createAuthor}
  ,CREATE_DATETIME = #{createDatetime}
  ,UPDATE_AUTHOR = #{updateAuthor}
  ,UPDATE_DATETIME = current_timestamp() 
WHERE 
   ALBUM_ID = #{albumId}
   AND TRACK_NO = #{trackNo}
   AND UPDATE_DATETIME = #{updateDatetime}

mapper.delete:
DELETE FROM ALBUM_TRACK
WHERE
   ALBUM_ID = #{albumId}
   AND TRACK_NO = #{trackNo}
   AND UPDATE_DATETIME = #{updateDatetime}

DELETE FROM ALBUM_TRACK
WHERE 1 = 1 
  AND ALBUM_ID = #{albumId}
  AND TRACK_NO = #{trackNo}
  AND TRACK_NAME = #{trackName}
  AND PLAY_TIME = #{playTime}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ALBUM_ID AS "albumId"
  ,TRACK_NO AS "trackNo"
  ,TRACK_NAME AS "trackName"
  ,PLAY_TIME AS "playTime"
  ,CREATE_AUTHOR AS "createAuthor"
  ,CREATE_DATETIME AS "createDatetime"
  ,UPDATE_AUTHOR AS "updateAuthor"
  ,UPDATE_DATETIME AS "updateDatetime"
FROM ALBUM_TRACK
WHERE 1 = 1 
  AND ALBUM_ID = #{albumId}
  AND TRACK_NO = #{trackNo}
  AND TRACK_NAME = #{trackName}
  AND PLAY_TIME = #{playTime}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}
   ORDER BY ${_theOrderByCondition}
  LIMIT ${_theFetchStart}, ${_theFetchSize}

SELECT count(*)
FROM ALBUM_TRACK
WHERE 1 = 1 
  AND ALBUM_ID = #{albumId}
  AND TRACK_NO = #{trackNo}
  AND TRACK_NAME = #{trackName}
  AND PLAY_TIME = #{playTime}
